import { MechParams } from "../params";
export class AesCcmParams {
    constructor(dataLength, nonce, aad = Buffer.alloc(0), macLength = 0) {
        this.type = MechParams.AesCCM;
        this.dataLength = dataLength;
        this.nonce = nonce;
        this.aad = aad;
        this.macLength = macLength;
    }
    toCKI() {
        return {
            aad: this.aad,
            dataLen: this.dataLength,
            macLen: this.macLength,
            nonce: this.nonce,
            type: this.type,
        };
    }
}
