import { MechanismEnum } from "../../mech_enum";
import { MechParams } from "../params";
import { RsaMgf } from "./mgf";
export class RsaPssParams {
    constructor(hashAlg = MechanismEnum.SHA1, mgf = RsaMgf.MGF1_SHA1, saltLen = 20) {
        this.type = MechParams.RsaPSS;
        this.hashAlgorithm = hashAlg;
        this.mgf = mgf;
        this.saltLength = saltLen;
    }
    toCKI() {
        return {
            hashAlg: this.hashAlgorithm,
            mgf: this.mgf,
            saltLen: this.saltLength,
            type: this.type,
        };
    }
}
